import React from "react";
import { Link } from "react-router-dom";


import "./index.css"



class ProductItem extends React.Component {
    constructor(){
        super();
        this.state = {
            currentDel: false
        }
    }
    setCurrent(value){
        if(value  == 1){
            this.setState({
                currentDel: true
            })
        } else {
            this.setState({
                currentDel: false
            })
        }
        console.lg
    }
    render() { 
        const {data, delProduct, productsListContext} = this.props
        const className = "product-card" + this.state.currentDel ? 'del' : null
        return (
            <article className={className}>
                <div>
                    <h3>название: {data.TITLE}</h3>
                    <img src={data.IMG} />
                    <p>описание: {data.DISCR}</p>
                    <span>цена: {data.PRICE}</span>
                    <br/>
                    <span>кол-во: {data.COUNT}</span>
                    <div>
                        <button onClick={() => delProduct(data.ID, productsListContext)}>
                            <>Удалить</>
                        </button>
                        <Link to={`/products/${data.ID}`}>Редактировать</Link>
                    </div>
                    <div>
                        <button onClick={() => this.setCurrent(1)}>+</button>
                        <br />
                        <button onClick={() => this.setCurrent(0)}>-</button>
                    </div>
                </div>
            </article>
        )
    }
}

export default ProductItem;
