import React from "react";


import productsJson from "../../stub/products.json"

import ProductItem from "../../components/product-item";

import "./index.css"

class Products extends React.Component{
    constructor(){
        super()
        this.state = {
            data: productsJson
        }
        this.findItem = this.findItem.bind(this)
    }
    findItem(event){
        event.preventDefault()
        let title = event.target[0].value
        let data = productsJson
        let array = []
        array.push(data.find(item => 
            item.TITLE == title
        ))
        if(array.length == 0){
            array = productsJson
        }
        this.setState({
            data : array
        })
    }
    //methods od each react`s component for depicting template
    render(){
        return (
                <section>
                    <h1>Catalog</h1>
                    <div>
                        <h2>Find smt...</h2>
                        <form onSubmit={this.findItem}>
                            <input type="text"/>
                            <input type="submit" value="найти"/>
                        </form>
                    </div>
                    <div className="products-wrapper"> { this.state.showResults ? <Results /> : null }
                        {
                            this.state.data.map(item => 
                                <ProductItem key={item.ID} data={item}/>
                            )
                        }
                    </div>
                </section>
            )

    }
}

export default Products;
