import ReactDOM from 'react-dom/client';
import React, {useState, useEffect} from 'react'

import './index.css'

import {Loader} from '../loader'

import productsJson from '../../stub/products.json'

import ProductItem from '../product-item/index'


export function ProductsList(props) {
    const [data, setData] = useState([])
    const [alwaysData, setAlwaysData] = useState(productsJson)
    const [isLoading, setIsLoading] = useState(true)
    const [showNothingFoundErr, setShowNothingFoundErr] = useState(false)
    useEffect(() => {
        setTimeout(() => {
            setData(productsJson)
            setIsLoading(false)
        }, 1000)
    }, [])
    const findItem = (event) => {
        event.preventDefault()
        let title = event.target[0].value
        let data = alwaysData
        let t_f = false
        let array = []
        if(title == ''){
            array = data
        } else {
            array.push(data.find(item => 
                item.TITLE.toLowerCase() == title.toLowerCase() || item.DISCR.toLowerCase() == title.toLowerCase()
            ))
            if(array[0] == undefined){
                array = data
                t_f = true
            } 
        }
        setData(array)
        setShowNothingFoundErr(t_f)
    }
    if(isLoading){
        return (
            <Loader />
        )
    }
    return (
        // onSubmit={this.findItem}
        //{this.state.showNothingFoundErr ? <strong className="err">Ничего не было найдено</strong> : null }
        <React.Fragment>
            <div>
                <h2>Find smt...</h2>
                <form>
                    <input type="text"/>
                    <input type="submit" value="найти"/>
                </form>
            </div>
                {this.state.showNothingFoundErr ? <strong className="err">Ничего не было найдено</strong> : null }
            <div className="products-wrapper">
                {
                    data.map(item => 
                        //delProduct={this.delProduct} productsListContext={this}
                        <ProductItem key={item.ID} data={item}/>
                    )
                }
            </div>
        </React.Fragment>
    )

}
