import ReactDOM from 'react-dom/client';
import React, {useState, useEffect} from 'react'

import './index.css'

import {Loader} from '../loader'

import productsJson from '../../stub/products.json'

import ProductItem from '../product-item/index'


export function ProductsList(props) {
    const [data, setData] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    useEffect(() => {
        setTimeout(() => {
            setData(productsJson)
            setIsLoading(false)
        }, 1000)
    }, [])
    if(isLoading){
        return (
            <Loader />
        )
    }
    return (
        <React.Fragment>
            <div>
                <h2>Find smt...</h2>
                //onSubmit={this.findItem}
                <form onSubmit={this.findItem}>
                    <input type="text"/>
                    <input type="submit" value="найти"/>
                </form>
            </div>
                {this.state.showNothingFoundErr ? <strong className="err">Ничего не было найдено</strong> : null }
            <div className="products-wrapper">
                {
                    data.map(item => 
                        //delProduct={this.delProduct} productsListContext={this}
                        <ProductItem key={item.ID} data={item}/>
                    )
                }
            </div>
        </React.Fragment>
    )

}
