import React from "react";


import productsJson from "../../stub/products.json"

import ProductItem from "../../components/product-item";

import "./index.css"

class Products extends React.Component{
    constructor(){
        super()
        this.state = {
            data: productsJson
        }
    }
    findItem(text){
        let data = this.state.data
        let array = data.find((item, i, data) => {
            let nd = []
            if(item.TITLE = text){
                nd.push(item)
            }
            return nd
        })
        this.setState({
            data : array
        })
    }
    //methods od each react`s component for depicting template
    render(){
        return (
                <section>
                    <h1>Catalog</h1>
                    <div>
                        <h2>Find smt...</h2>
                        <input type="text"/>
                        <input type="submit" onClick={() => {this.findItem("Куртка")}} />
                    </div>
                    <div className="products-wrapper">
                        {
                            this.state.data.map(item => 
                                <ProductItem data={item}/>
                            )
                        }
                    </div>
                </section>
            )

    }
}

export default Products;
