import React from "react";

import './index.css'

import goodsJSON from '../../stub/products.json'

class ProductDetail extends React.Component {
    constructor(){
        super();
    }
    render() {
        const prId = window.location.pathname.replace('/products/', '')
        const data = goodsJSON.find(item => item.ID == prId)
        return(
            <>
                <article>
                        <h3>название: {data.TITLE}</h3>
                        <img src={data.IMG} />
                        <form>
                            Название товара <input type="text" name="title"/><br></br>
                            Описание товара <input /><br></br>
                            Цена товара <input /><br></br>
                            Количество товара <input />
                            Изображение товара <input />
                        </form>
                </article>
            </>
        )
    }
}

export default ProductDetail;
