import React from "react";


import productsJson from "../../stub/products.json"

import ProductItem from "../../components/product-item";

import "./index.css"

class Products extends React.Component{
    constructor(){
        super()
        this.state = {
            data: productsJson,
            fStr: ""
        }
        this.findItem = this.findItem.bind(this)
    }
    findItem(event){
        this.setState({
            fStr : event.target.value
        })
        let data = this.state.data
        let array = []
        array.push(data.find(item => {
            let nd = []
            if(item.TITLE == this.state.fStr){
                nd.push(item)
                console.log("🚀 ~ file: index.js:29 ~ Products ~ findItem ~ nd", nd)
            }
            return nd
        }))

        console.log("🚀 ~ file: index.js:25 ~ Products ~ findItem ~ array", array)
        this.setState({
            data : array
        })
    }
    //methods od each react`s component for depicting template
    render(){
        return (
                <section>
                    <h1>Catalog</h1>
                    <div>
                        <h2>Find smt...</h2>
                        <input type="text" value={this.state.fStr} onChange={this.findItem}/>
                    </div>
                    <div className="products-wrapper">
                        {
                            this.state.data.map(item => 
                                <ProductItem key={item.ID} data={item}/>
                            )
                        }
                    </div>
                </section>
            )

    }
}

export default Products;
