import React from "react";


import productsJson from "../../stub/products.json"

import ProductItem from "../../components/product-item";

import "./index.css"

class Products extends React.Component{
    constructor(){
        super()
        this.state = {
            data: productsJson,
            showNothingFoundErr: false
        }
        this.findItem = this.findItem.bind(this)
    }
    findItem(event){
        event.preventDefault()
        let title = event.target[0].value
        let data = productsJson
        let t_f = false
        let array = []
        array.push(data.find(item => item.TITLE.toLowerCase == title.toLowerCase || item.DISCR.toLowerCase == title.toLowerCas ))
        if(array[0] == undefined){
            array = productsJson
            t_f = true
        } 
        this.setState({
            data : array,
            showNothingFoundErr: t_f
        })        
    }
    //methods od each react`s component for depicting template
    render(){
        return (
                <section>
                    <h1>Catalog</h1>
                    <div>
                        <h2>Find smt...</h2>
                        <form onSubmit={this.findItem}>
                            <input type="text"/>
                            <input type="submit" value="найти"/>
                        </form>
                    </div>
                    {this.state.showNothingFoundErr ? <strong className="err">Ничего не было найдено</strong> : null }
                    <div className="products-wrapper">
                        {
                            this.state.data.map(item => 
                                <ProductItem key={item.ID} data={item}/>
                            )
                        }
                    </div>
                </section>
            )

    }
}

export default Products;
